import React, {useState, useEffect} from 'react'
import Tile from "./Tile";
import NavBar from './NavBar';
import SearchBar from './SearchBar';
import axios from "axios";
import './LandingPage.css'



function LandingPage({data}) {
    console.log(data);
    const [binfo, setBinfoState] = useState([])

    function getData() {
        axios.get("http://localhost:3003/getdata") //need to add the params later
        .then( res =>{ setBinfoState(res.data);})
        .catch( err => console.log(err))
    }

    useEffect(() => {
        getData();
    }, [])
    return(
        <>
            <div className="search">
        <div className="searchInputs">
            <form onSubmit={onsubmit}>
            <input
            type="text"
            placeholder={placeholder}
            value={wordEntered}
            onChange={handleFilter}
            />
            </form>
            <div className="searchIcon">
            {flag ? (
                <IoSearch />
            ) : (
                <IoClose id="clearBtn" onClick={clearInput} />
            )}
            </div>
        </div>
        {(
            <div className="dataResult">
            {filteredData.slice(0, 15).map((value, key) => {
                return (
                <a className="dataItem" href={value.link} target="_blank">
                    <p>{value.businessName} </p>
                </a>
                );
            })}
            {filteredDataIndustry.slice(0, 15).map((value, key) => {
                return (
                <a className="dataItem" href={value.link} target="_blank">
                    <p>{value.businessName} </p>
                </a>
                );
            })}
            {filteredDataNeighbourhood.slice(0, 15).map((value, key) => {
                return (
                <a className="dataItem" href={value.link} target="_blank">
                    <p>{value.businessName} </p>
                </a>
                );
            })}
            {filteredDataTags.slice(0, 15).map((value, key) => {
                return (
                <a className="dataItem" href={value.link} target="_blank">
                    <p>{value.businessName} </p>
                </a>
                );
            })}
            </div>
        )}
        </div>
        <NavBar />
        <div className="container2">
            {binfo.map(bis => <a href={"/getuser/"+bis._id}> <Tile key={bis._id} name={bis.businessName} 
            description={bis.description} image={bis.image} visible="green" /> </a> )}            
        </div>

        </>
    );
}
export default LandingPage;
