import './Tile.css'

function Tile({name, description, image, visible}) {

    function handleClick(e) {
        let color = e.target.style.backgroundColor;
        color === "lightgreen" ? e.target.style.backgroundColor = "red" : e.target.style.backgroundColor = "lightgreen";
    }

    return (
        <>
            <div className="container">
                <div className="backgroundImage">
                    <img src={"data:image;base64,"+{image}.image} alt="backgroundPhoto" class="bg" />
                </div>
                <div className="businessName">
                    <span class="name">{name} </span>
                    <input type="button" value="V" style={{backgroundColor:"lightgreen"}} onClick={e => handleClick(e)} />

                </div>
                <div className="description">
                    {description}
                </div>

            </div>
        </>
    );
}
export default Tile;
